import React from 'react'

const test = () =>{
    const [data, setData] = useState(null);
    const jeton = 'eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI3Y2Q5ZGQ0NWQ3NmE5ODE1YTRmMjVkZDg2OTgwMmUwZSIsInN1YiI6IjY1NTY1OWMwN2YwNTQwMDEzOWVjNjkxZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.tIKiwXA0XiCDV1Ad5uBCr0TOGW59ew5KWzl8LZZAk1o'; // Remplacez ceci par votre véritable jeton d'authentification

    useEffect(() => {
      // Effectue la requête API lors du montage du composant
      const fetchData = async () => {
        try {
          const response = await fetch('https://api.themoviedb.org/3/authentication', {
            headers: {
              'Authorization': `Bearer ${jeton}`,
              'Content-Type': 'application/json',
              // Ajoutez d'autres en-têtes si nécessaire
            },
          });
          const jsonData = await response.json();
          setData(jsonData);
        } catch (error) {
          console.error('Erreur lors de la récupération des données depuis l\'API', error);
        }
      };

      fetchData();
    }, [jeton]); // Ajoutez le jeton en tant que dépendance pour que useEffect se réexécute si le jeton change

    return (
      <div>
        <h1>Données de l'API</h1>
        {data && data.results && data.results.length > 0 && (
          <div>
            <h2>Titre du premier élément:</h2>
            <p>{data.results[0].title}</p>
            {/* Ajoutez ici d'autres informations que vous souhaitez afficher */}
          </div>
        )}
      </div>
    );
}

export default test
