      const value = e.target.value;
      setInputValue(e.target.value);

    };

  useEffect(() => {
    // Effectue la requête API lors du montage du composant
    const fetchData = async () => {
      try {

          if(inputValue===''){
              const response = await fetch('https://api.themoviedb.org//3/discover/movie', {
              headers: {
                'Authorization': `Bearer ${jeton}`,
                'Content-Type': 'application/json',
                // Ajoutez d'autres en-têtes si nécessaire
              },
            });
            const jsonData = await response.json();
            setData(jsonData);
            console.log('if');
          }
          else{
              const response = await fetch(`https://api.themoviedb.org/3/search/movie?query=${inputValue}`, {
              headers: {
                'Authorization': `Bearer ${jeton}`,
                'Content-Type': 'application/json',
                // Ajoutez d'autres en-têtes si nécessaire
              },
            });
            const jsonData = await response.json();
            setData(jsonData);
            console.log('else');
          }





      } catch (error) {
        console.error('Erreur lors de la récupération des données depuis l\'API', error);
      }
    };

    fetchData();
  }, [jeton,inputValue]); // Ajoutez le jeton en tant que dépendance pour que useEffect se réexécute si le jeton change

  return (
    <Router>
      <Switch>
        <Route path="/" exact>
          <Test data={data} inputChange={inputChange} />
        </Route>

      </Switch>
    </Router>
  );
}

export default App;
