import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useQuery } from 'react-query';
import './App.css';
import Test from './test';
import MovieDetail from './MovieDetail';
import MovieAPI from './api/MovieApi'
import MovieSearchApi from './api/MovieSearchApi'
const App = () => {

  const jeton = 'eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI3Y2Q5ZGQ0NWQ3NmE5ODE1YTRmMjVkZDg2OTgwMmUwZSIsInN1YiI6IjY1NTY1OWMwN2YwNTQwMDEzOWVjNjkxZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.tIKiwXA0XiCDV1Ad5uBCr0TOGW59ew5KWzl8LZZAk1o'; // Remplacez ceci par votre véritable jeton d'authentification
  const [inputValue, setInputValue] = useState('');
  const inputChange = (e) => {
      const value = e.target.value;
      setInputValue(e.target.value);

    };

    const { data: movies, error, isLoading } = useQuery('movies', MovieAPI);
    const [data, setData] = useState(null);
    useEffect(() => {
      if(inputValue===''){
        setData(movies);
      }
      else{
        const response =  fetch(`https://api.themoviedb.org/3/search/movie?query=${inputValue}`, {
          headers: {
            'Authorization': `Bearer ${jeton}`,
            'Content-Type': 'application/json',
            // Ajoutez d'autres en-têtes si nécessaire
          },
        });
        const jsonData =  response.json();
        setData(jsonData);
        console.log('else'+data);
      }

    }, [inputValue,movies]);


  console.log('salam'+data);


  // Ajoutez le jeton en tant que dépendance pour que useEffect se réexécute si le jeton change

  return (

    <Router>
    <Routes>
      <Route path="/" element={<Test data={data}  inputChange={inputChange} />} />
      <Route path="/movie/:id" element={<MovieDetail data={data}  />} />
    </Routes>
  </Router>
  );
}

export default App;
