            setData(jsonData);
            console.log('else'+data);






      } catch (error) {
        console.error('Erreur lors de la récupération des données depuis l\'API', error);
      }
    };

    fetchData();
  }
  }, [movies,inputValue]); 


  useEffect(() => {
    const storedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    console.log("storedFavorites");
    console.log(storedFavorites);
    setFavorites(storedFavorites);
  }, []);


  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);
  if (error) {
    return <p>Erreur</p>;
  }

  if (!data) {
    return <p>ooooooppppppsssss pas de film</p>;
  }
  return (

    <Router>
    <Routes>
      <Route path="/" element={<Movies data={data}  inputChange={inputChange}  />} />
      <Route path="/movie/:id" element={<MovieDetail data={data} addFavorites={addFavorites} favorites={favorites} />} />
      <Route path="/movie/favoris" element={<Favoris data={favorites}  />} />
    </Routes>
  </Router>
  );
}

export default App;
